data = data.frame(subRegion = c("Antarctica"),                  value = c(0))rmap::map(data,          underLayer = rmap::mapGCAMBasins,          overLayer = rmap::mapCountries,          underLayerLabels = T,          underLayerColor = "red",          underLayerLwd = 0.5,          save = T,          folder = "figures/rmaps/basin_names_countries",          crop = F,          width = 50, height =50)